import React, { useEffect, useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import {
  faGithub,
  faLinkedin,
  faMedium,
  faStackOverflow,
} from "@fortawesome/free-brands-svg-icons";
import { Box, HStack } from "@chakra-ui/react";

const socials = [
  {
    icon: faEnvelope,
    name: "Envelope Icon",
    url: "mailto: hello@example.com",
  },
  {
    icon: faGithub,
    name: "Github Logo",
    url: "https://github.com",
  },
  {
    icon: faLinkedin,
    name: "Linkedin Logo",
    url: "https://www.linkedin.com",
  },
  {
    icon: faMedium,
    name: "Medium Logo",
    url: "https://medium.com",
  },
  {
    icon: faStackOverflow,
    name: "StackOverflow Logo",
    url: "https://stackoverflow.com",
  },
];

const Header = () => {
  const handleClick = (anchor) => () => {
    const id = `${anchor}-section`;
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  };

  return (
    <Box
      position="fixed"
      top={0}
      left={0}
      right={0}
      translateY={0}
      transitionProperty="transform"
      transitionDuration=".3s"
      transitionTimingFunction="ease-in-out"
      backgroundColor="#18181b"
    >
      <Box color="white" maxWidth="1280px" margin="0 auto">
        <HStack
          px={16}
          py={4}
          justifyContent="space-between"
          alignItems="center"
        >
          <nav>
            <a href="mailto: hello@example.com" target="_blank" alt="Envelope Icon"><FontAwesomeIcon icon="fa-regular fa-envelope" /></a>
            <a href="https://github.com" target="_blank" alt="Github Logo"><FontAwesomeIcon icon="fa-brands fa-github" /></a>
            <a href="https://www.linkedin.com" target="_blank" alt="Linkedin Logo"><FontAwesomeIcon icon="fa-brands fa-linkedin" /></a>
            <a href="https://medium.com" target="_blank" alt="Medium Logo"><FontAwesomeIcon icon="fa-brands fa-medium" /></a>
            <a href="https://stackoverflow.com" target="_blank" alt="StackOverflow Logo"><FontAwesomeIcon icon="fa-brands fa-stack-overflow" /></a>
          </nav>
          <nav>
            <HStack spacing={8}>
              {/* Add links to Projects and Contact me section */}
            </HStack>
          </nav>
        </HStack>
      </Box>
    </Box>
  );
};
export default Header;
