import React, { useEffect, useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import {
  faGithub,
  faLinkedin,
  faMedium,
  faStackOverflow,
} from "@fortawesome/free-brands-svg-icons";
import { Box, HStack } from "@chakra-ui/react";

const socials = [
  {
    icon: faEnvelope,
    name: "Envelope Icon",
    url: "mailto: hello@example.com",
  },
  {
    icon: faGithub,
    name: "Github Logo",
    url: "https://github.com",
  },
  {
    icon: faLinkedin,
    name: "Linkedin Logo",
    url: "https://www.linkedin.com",
  },
  {
    icon: faMedium,
    name: "Medium Logo",
    url: "https://medium.com",
  },
  {
    icon: faStackOverflow,
    name: "StackOverflow Logo",
    url: "https://stackoverflow.com",
  },
];

const SocialList = () => (
  <div>
    {socials.map(social => (<Social key={social.icon} icon={social.icon} name={social.name} url={social.url} /> ))}
  </div>
);

const Header = () => {
  const handleClick = (anchor) => () => {
    const id = `${anchor}-section`;
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({
        behavior: "smooth",
        block: "start",
      });
    }
  };

  return (
    <Box
      position="fixed"
      top={0}
      left={0}
      right={0}
      translateY={0}
      transitionProperty="transform"
      transitionDuration=".3s"
      transitionTimingFunction="ease-in-out"
      backgroundColor="#18181b"
    >
      <Box color="white" maxWidth="1280px" margin="0 auto">
        <HStack
          px={16}
          py={4}
          justifyContent="space-between"
          alignItems="center"
        >
          <nav>
            <HStack spacing={4}>
              <a href={socials.url} alt={socials.name}><FontAwesomeIcon icon={faEnvelope} /></a>
              <a href={socials.url} alt={socials.name}><FontAwesomeIcon icon={faGithub} /></a>
              <a href={socials.url} alt={socials.name}><FontAwesomeIcon icon={faLinkedin} /></a>
              <a href={socials.url} alt={socials.name}><FontAwesomeIcon icon={faMedium} /></a>
              <a href={socials.url} alt={socials.name}><FontAwesomeIcon icon={faStackOverflow} /></a>
            </HStack>
          </nav>
          <nav>
            <HStack spacing={8}>
              {/* Add links to Projects and Contact me section */}
            </HStack>
          </nav>
        </HStack>
      </Box>
    </Box>
  );
};
export default Header;
